            }
           this.navbars[index].fill = '#2075AC'
        },
        drawer(index, name) {
            this.isactive = index
            console.log(this.isactive)
            if (status === name) {
                this.ifslide = false
                status = undefined
            } else {
                this.$store.state.datasummary = false
                this.ifslide = true
                status = name

            }
            for (let key in this.controls) {
                this.controls[key] = false
            }
            this.controls[`${name}`] = true

        }
    }
